//SERVICES

const serviceMenuList = document.querySelector(".service-section .menu-list")
const serviceMenuItems = document.querySelectorAll(".service-section .menu-item")
const serviceInfo = document.querySelectorAll(".service-section .info")


const showInfo = (event) => {
    serviceMenuItems.forEach((item) => {
        item.classList.remove("active")
    })
    event.target.classList.add("active");

    serviceInfo.forEach((item) => {
        if (item.dataset.title === event.target.dataset.title) {
            item.classList.add("active")
        } else {
            item.classList.remove("active")
        }
    })
}

serviceMenuList.addEventListener("click", showInfo)


//AMAZING WORK


const categoryList = document.querySelector(".amazing-work-section .menu-list");
const categoryItems = document.querySelectorAll(".amazing-work-section .menu-item");
let currentCategory = "all"

const cardWrap = document.querySelector(".amazing-work-section .image-wrap")

const loadMoreButton = document.querySelector(".amazing-work-section .button")



const filterCards = (card) => {
    if (currentCategory === "all" || card.dataset.category === currentCategory) {
        card.classList.remove("hidden")
    } else {
        card.classList.add("hidden")
    }
}

const changeCategory = (event) => {
    const cardItems = document.querySelectorAll(".amazing-work-section .image-wrap li");
    categoryItems.forEach((item) => {
        item.classList.remove("active")
    })
    event.target.classList.add("active");
    currentCategory = event.target.dataset.category;
    cardItems.forEach(filterCards)
}

