const aboutMenuItems = document.querySelectorAll(".about-section .menu-item");
const reviewCollection = document.querySelectorAll(".about-section .review");
const arrowLeft = document.querySelector(".about-section .arrow.left");
const arrowRight = document.querySelector(".about-section .arrow.right");

let currentItem = 0;

const changeActive = (n) => {
    document.querySelector(".about-section .review.active").classList.remove("active");
    document.querySelector(".about-section .menu-item.active").classList.remove("active");
    aboutMenuItems[n].classList.add("active");
    reviewCollection[n].classList.add("active");
}

aboutMenuItems.forEach((element, i) => {
   element.addEventListener("click", (event) => {
       currentItem = i;
       changeActive(currentItem)
   })
})

arrowLeft.addEventListener("click", () => {
    if (currentItem -1 < 0) {
        currentItem = aboutMenuItems.length -1;
    } else {
        currentItem = currentItem - 1;
    }
    changeActive(currentItem)
});

arrowRight.addEventListener("click", () => {
    if (currentItem +1 > aboutMenuItems.length -1) {
        currentItem = 0;
    } else {
        currentItem = currentItem + 1;
    }
    changeActive(currentItem)
})